mapper.select:
select
    cf.*
from
    chat_file cf left outer join chat_file_usage cfu on (cf.id = cfu.file_id)

    cf.uploader_id = #{userId}
    and cfu.usage_type = 1 and cfu.usage_id = #{workspaceId}
    and cfu.usage_type = 2 and cfu.usage_id = #{channelId}
    and cfu.usage_type = 3 and cfu.usage_id = #{messageId}

mapper.insert:
insert into chat_file_usage (
    usage_type, usage_id, file_id
) values (
    #{usageType},
    #{usageId},
    #{fileId}
)
on duplicate key update id = id
